mapper.select:
SELECT s.*
FROM schedule s
         JOIN category c ON s.category_id = c.category_id
WHERE c.member_id = #{memberId}
  AND s.schedule_deadline BETWEEN #{start} AND #{end}

SELECT COUNT(*)
FROM schedule s
         JOIN category c ON s.category_id = c.category_id
WHERE c.member_id = #{memberId}
  AND s.schedule_deadline BETWEEN #{start} AND #{end}
  AND s.completion_status = 1
